import { Component, OnInit } from '@angular/core';
import { PASTRIES, Max } from '../mock-pastries';
import { Paginate, Pastrie, PreferencePastries } from '../pastrie';
import { PastriesService } from '../pastries.service';

@Component({
  selector: 'app-pastries',
  templateUrl: './pastries.component.html',
  styleUrls: ['./pastries.component.scss'],
})
export class PastriesComponent implements OnInit {
  pastries: Pastrie[] = [];
  titlePage: string = 'Page principale : liste des pâtisseries à gagner';
  preferencePastries: PreferencePastries[] = [];
  count: number = 0;
  color: string = "#009688";


  currentPastrie: Pastrie | null = null;

  constructor(private ps: PastriesService) {
    // console.log('Constructor');
  }

  ngOnInit(): void {
    // console.log('Monter dans le DOM ...');

    this.ps.paginate(0, 3).subscribe(pastries => {
      this.pastries = pastries;
    })
  }

  // (click) = récupère la pastrie
  onSelect(pastrie: Pastrie): void {
    console.log(pastrie);

    this.currentPastrie = pastrie;
  }

  changeParentPreference($event: string): void {
    console.log($event);

    const pastrie: Pastrie | undefined = this.pastries.find(
      (p) => p.id == $event
    );

    if (pastrie) {
      pastrie.choice = !pastrie.choice;

      if (pastrie.choice && this.count < Max) this.count++;
      if (!pastrie.choice && this.count > 0) this.count--;

    }
  }

  search($event: Pastrie[]): void {
    this.pastries = $event;
  }

  paginate($event: Paginate): void {
    const { start, end } = $event;
    this.ps.paginate(start, end).subscribe(pastries => this.pastries = pastries);
  }


addPastrie(): void {
  const newPastrie: Pastrie = {
    id: '',
    ref: '',
    name: '',
    description: '',
    url: '',
    quantity: 0,
    order: 0,
    like: '',
    choice: false,
  };
  this.ps.addOrUpdateRecipe(newPastrie).subscribe((pastrie) => {
    if (pastrie) {
      console.log('Recette ajoutée:', pastrie);
    } else {
      console.log("Erreur lors de l'ajout de la recette");
    }
  })
}
modifyPastrie() {
  if(this.currentPastrie){
    this.ps.addOrUpdateRecipe(this.currentPastrie).subscribe(pastrie =>{
      console.log('Recette modifiée:', pastrie);
    });
  }
}

<app-search (searchPastries)="search($event)" ></app-search>
<div class="row content h-pastries">
  <!-- du template vers le TS (event)="function(arg)" -->

  <!-- .pagination -->
  <app-pagination (paginate)="paginate($event)"></app-pagination>
  <app-pagination  (paginate)="paginate($event)"></app-pagination>
  <!-- On récupère la valeur $event de l'enfant -->
  <div class="col-sm-4">
    <div class="sticky">
    <app-pastrie-details 
    [pastrie]="currentPastrie" 
    [count]="count"
    (changePreference)="changeParentPreference($event)"
    ></app-pastrie-details>
  </div>
  </div>
  <div class="col-sm-8 pastrie">
    <section *ngIf="pastries.length > 0">
      <article
        *ngFor="let pastrie of pastries"
        class="card on-select"
        (click)="onSelect(pastrie)"
        [appBorderCard]="color"
      >
        <header class="card-header">
          <h2>{{ pastrie.ref }}</h2>
          <button (click)="addPastrie()">Ajouter une recette</button>
          <button (click)="modifyPastrie()">Modifier une recette</button>
        </header>

        <section class="card-body">
          <h5 class="card-title">{{ pastrie.name }}</h5>
          <p class="card-text">{{ pastrie.description }}</p>
          <ul
            class="d-flex list-unstyled"
            *ngIf="pastrie.tags && pastrie.tags.length > 0"
          >
            <li
              class="bg-info text-light m-2 p-2 rounded"
              *ngFor="let tag of pastrie.tags"
            >
              {{ tag }}
            </li>
          </ul>
          <a href="#" class="btn btn-primary"> Desc >> </a>
          <img class="glyph-icon" src="./assets/svg/si-glyph-heart.svg" />
          <small>{{ pastrie.like }}</small>
          <img
            class="glyph-icon"
            src="./assets/svg/si-glyph-triangle-double-arrow-right.svg"
          />
          <small>{{ pastrie.quantity }} {{ pastrie.quantity > 2 ? 'unités disponbiles' : 'unité disponbile'}} </small>
          <small *ngIf="pastrie.choice">[priority]</small>
        </section>
      </article>
    </section>
  </div>
  <!-- du template vers le TS (event)="function(arg)" -->
  <app-pagination  (paginate)="paginate($event)" ></app-pagination>
  <!-- .pagination -->
</div>
